import React from "react";
import "../../styles/Cards.css";
import CountUp from "react-countup";
import { Typography, Grid, Card, CardContent } from "@material-ui/core";
const Presentation = ({
  data: { confirmed, recovered, deaths, lastUpdate },
}) => {
  // const { data } = props;
  // console.log(data.confirmed.value);
  //confirmed
  //recovered
  //deaths
  //lastUpdate
  if (!confirmed) {
    return "loading........";
  }
  return (
    <div className="container">
      <Grid container spacing={3} justify="center">
        <Grid item component={Card}>
          <CardContent>
            <Typography colour="textSecondary">infected </Typography>
            <Typography variant="h5">
              <CountUp
                start={0}
                end={confirmed.value}
                duration={2.5}
                separator=","
              />
            </Typography>
            <Typography color="textSecondary">real data</Typography>
            <Typography colour="textSecondary">
              {new Date(lastUpdate).toDateString()}
            </Typography>
            <Typography variant="body2">
              number of infected cases of covid -19
            </Typography>
          </CardContent>
        </Grid>
        <Grid item component={Card}>
          <CardContent>
            <Typography colour="textSecondary">recovered</Typography>
            <Typography variant="h5">
              <CountUp
                start={0}
                end={recovered.value}
                duration={2.5}
                separator=","
              />
            </Typography>
            <Typography colour="textSecondary">
              {new Date(lastUpdate).toDateString()}
            </Typography>
            <Typography variant="body2">
              number of recovered cases of covid -19
            </Typography>
          </CardContent>
        </Grid>
        <Grid item component={Card}>
          <CardContent>
            <Typography colour="textSecondary">deaths</Typography>
            <Typography variant="h5">
              <CountUp
                start={0}
                end={deaths.value}
                duration={2.5}
                separator=","
              />
            </Typography>
            <Typography colour="textSecondary">
              {new Date(lastUpdate).toDateString()}
            </Typography>
            <Typography variant="body2">
              number of deaths cases of covid -19
            </Typography>
          </CardContent>
        </Grid>
      </Grid>
    </div>
  );
};
export default Presentation;
